import React, { useEffect, useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import api from '../api'

const ActionDetail = () => {
  const { id } = useParams()
  const [action, setAction] = useState(null)
  const navigate = useNavigate()

  useEffect(() => {
    api.get(`/actions/${id}`)
      .then(res => setAction(res.data))
      .catch(() => {
        alert('Ошибка загрузки действия')
        navigate('/actions')
      })
  }, [id, navigate])

  if (!action) return <div>Загрузка...</div>

  return (
    <div style={{ maxWidth: 800, margin: '20px auto' }}>
      <h2>Действие #{action.action_id}</h2>
      <p><b>Код угрозы:</b> {action.threatlevel_id}</p>
      <p><b>Протокол действий:</b> {action.protocol}</p>
      <button onClick={() => navigate('/actions')}>Назад</button>
    </div>
  )
}

export default ActionDetail
