import React, { useEffect, useState } from 'react';
import api from '../api';

const Roles = () => {
  const [roles, setRoles] = useState([]);
  const [newRole, setNewRole] = useState('');
  const [editingRoleId, setEditingRoleId] = useState(null);
  const [editingRoleName, setEditingRoleName] = useState('');

  useEffect(() => {
    fetchRoles();
  }, []);

  const fetchRoles = () => {
    api.get('/roles')
      .then(res => setRoles(res.data))
      .catch(() => alert('Ошибка загрузки ролей'));
  };

  const createRole = () => {
    api.post('/roles', { name: newRole })
      .then(() => {
        setNewRole('');
        fetchRoles();
      })
      .catch(() => alert('Ошибка создания роли'));
  };

  const deleteRole = (id) => {
    api.delete(`/roles/${id}`)
      .then(fetchRoles)
      .catch(() => alert('Ошибка удаления'));
  };

  const updateRole = () => {
    api.put(`/roles/${editingRoleId}`, { name: editingRoleName })
      .then(() => {
        setEditingRoleId(null);
        setEditingRoleName('');
        fetchRoles();
      })
      .catch(() => alert('Ошибка обновления'));
  };

  return (
    <div style={{ maxWidth: 800, margin: '20px auto' }}>
      <h2>Роли</h2>

      <ul>
        {roles.map(role => (
          <li key={role.role_id}>
            {editingRoleId === role.role_id ? (
              <>
                <input
                  value={editingRoleName}
                  onChange={e => setEditingRoleName(e.target.value)}
                />
                <button onClick={updateRole}>Сохранить</button>
                <button onClick={() => setEditingRoleId(null)}>Отмена</button>
              </>
            ) : (
              <>
                {role.name}
                <button onClick={() => {
                  setEditingRoleId(role.role_id);
                  setEditingRoleName(role.name);
                }}>Редактировать</button>
                <button onClick={() => deleteRole(role.role_id)}>Удалить</button>
              </>
            )}
          </li>
        ))}
      </ul>

      <h3 style={{ marginTop: 30 }}>Добавить новую роль</h3>
      <input
        placeholder="Название роли"
        value={newRole}
        onChange={e => setNewRole(e.target.value)}
      />
      <button onClick={createRole}>Создать</button>
    </div>
  );
};

export default Roles;
