import React, { useEffect, useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import api from '../api'

const RoleDetail = () => {
  const { id } = useParams()
  const [role, setRole] = useState(null)
  const [newName, setNewName] = useState('')
  const navigate = useNavigate()

  useEffect(() => {
    api.get(`/roles/${id}`)
      .then(res => {
        setRole(res.data)
        setNewName(res.data.name)
      })
      .catch(() => {
        alert('Ошибка загрузки роли')
        navigate('/roles')
      })
  }, [id, navigate])

  const handleUpdate = async () => {
    try {
      await api.put(`/roles/${id}`, { name: newName })
      alert('Роль обновлена')
      navigate('/roles')
    } catch {
      alert('Ошибка обновления')
    }
  }

  const handleDelete = async () => {
    if (!window.confirm('Удалить эту роль?')) return
    try {
      await api.delete(`/roles/${id}`)
      alert('Роль удалена')
      navigate('/roles')
    } catch {
      alert('Ошибка удаления')
    }
  }

  if (!role) return <div>Загрузка...</div>

  return (
    <div style={{ maxWidth: 800, margin: '20px auto' }}>
      <h2>Роль #{role.role_id}</h2>
      <p><b>Название:</b></p>
      <input
        value={newName}
        onChange={e => setNewName(e.target.value)}
      />
      <br /><br />
      <button onClick={handleUpdate}>Сохранить</button>
      <button onClick={handleDelete} style={{ marginLeft: 10, color: 'red' }}>Удалить</button>
      <br /><br />
      <button onClick={() => navigate('/roles')}>Назад</button>
    </div>
  )
}

export default RoleDetail
